import { Item } from './styled';
import { Link } from 'react-router-dom';
import Modal from "react-bootstrap/Modal";
import React, { useState } from 'react';

export default (props) => {
    const api = useApi();
    const [isOpen, setIsOpen] = useState(false);
        // dados do usuario
        const [info, setUserInfo] = useState({});
        const [adList, setAdList] = useState([]);
    
    
        //dados para edição dos dados do usuario
        const [name, setName] = useState('');
        const [email, setEmail] = useState('');
        const [state, setState] = useState([]);
        const [password, setPassword] = useState('');
    
        //dados para edição de anuncios
        const [categories, setCategories] = useState([]);
        const fileField = useRef();

    const handleClick = () => {
        window.location.href = `/ad/${props.data.id}`;
    }

    const showModal = () => {
        setIsOpen(true);
    }
    const hideModal = () => {
        setIsOpen(false);
    };

    const handleNewAd = () => {

    };

    let price = '';
    if (props.data.priceNegotiable) {
        price = "Preço negociável";
    } else {
        price = `R$ ${props.data.price}`
    }


    return (
        <Item className="aditem">
            <Link to={`/ad/${props.data.id}`} onClick={handleClick}>
                <div className="p-2 m-2">
                    <div className="item-image">
                        {
                            props.data.images &&
                            props.data.images.map((image, index) =>
                                <img key={index} src={`http://alunos.b7web.com.br:501/media/${image.url}`} alt="" />)
                        }
                        <img src={props.data.image} alt="" />
                    </div>
                    <div className="item-name">
                        {props.data.title}
                    </div>
                    <div className="item-price">
                        {price}
                    </div>
                </div>

            </Link>
            <button onClick={showModal} type="button" class="btn btn-primary">Editar</button>
            <Modal show={isOpen} onHide={hideModal}>
                <Modal.Header>Edite seu anúncio</Modal.Header>
                <form>
                    <Modal.Body>
                        <div class="mb-3">
                            <label for="exampleInputEmail1" class="form-label">
                                Novo e-mail:
                                <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                                <div id="emailHelp" class="form-text">Seu e-mail antigo é: {props.data.title}</div>
                            </label>

                        </div>
                    </Modal.Body>
                    <Modal.Footer>
                        <button type="button" className="btn btn-danger" onClick={hideModal}>Cancel</button>
                        <button type="submit" onClick={handleNewAd} className="btn btn-success">Save</button>
                    </Modal.Footer>
                </form>
            </Modal>
        </Item>
    );
}
