import styled from 'styled-components';

export const SearchArea = styled.div`
background-color:#DDD;
border-bottom:#CCC;
padding:20px 0;

.searchBox{
    background-color:#670696;
    padding:20px 15px;
    display:flex;
    justify-content:center;
    align-items:center;
    border-radius:5px;
    box-shadow: 1px 1px 1px 0.3px rgba (0,0,0, 0.2);

    form{
        flex:1;
        display:flex;
        justify-content:center;

        input, select{
            height:40px;
            border:0;
            border-radius:5px;
            outline:0;
            font-size:15px;
            color:#000;
            margin-right:30px;
            padding:10px;
        }
        input{
            flex:1;
            padding:0 10px;
        }
        select{
            width:100px;
        }
        button{
            background-color:#288F00;
        }
    }
}

`;
export const PageArea = styled.div`


`;
