import styled from 'styled-components';

export const Item = styled.div`
a{
    display:block;
    text-decoration:none;
    margin:10px;
    border:1px solid #FFF;
    color:#000;
    padding:10px;
    background-color:#FFF;
    transition:all ease .3s;
    border-radius:5px;

    &:hover{
        border:1px solid #AE2BB2;
        background-color:#C98DCB;
    }

    .item-image img{
        width:100%;
        border-radius:5px;
    }
    .item-name{
        font-weight:bold;
    }
    .item-price{

    }
}
.btnGroup{
    margin-top:5px;

    .editButton, .deletButton{
        background:none;
        border-radius:5px;
        margin:5px;
        padding:5px;
    }
}

`;
