import styled from 'styled-components';

export const PageArea = styled.div`
.userInfo, .user{
    display:flex;
    flex-flow:column;
    align-items:center;
}
.avatar img{
    width:100px;
    height:100px;
    border-radius:50%;
    object-fit:cover;
}
.userInfosAndEdit{
    display:flex;
}
.actualInfos, .editInfos{
    flex:1;
    text-align:center;
}


`;
