import React, { useState, useRef, useEffect } from 'react';
import { PageArea } from './styled';
import { PageContainer } from '../../components/MainComponents';
import useApi from '../../helpers/MBSApi';
import EditItem from '../../components/partials/EditItem';
import Modal from '../../components/partials/Modal';




const Page = () => {
    const api = useApi();

    // dados do usuario
    const [info, setUserInfo] = useState({});
    const [adList, setAdList] = useState([]);


    //dados para edição dos dados do usuario
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [state, setState] = useState([]);
    const [password, setPassword] = useState('');

    //dados para edição de anuncios
    const [categories, setCategories] = useState([]);
    const [visibleModal, setVisibleModal] = useState(false);
    const [adStatusModal, setAdStatusModal] = useState(false);
    const [adTitleModal, setAdTitleModal] = useState("");

    const [categoryModal, setCategoryModal] = useState("");
    const [priceModal, setPriceModal] = useState("");
    const [priceNegotiableModal, setPriceNegotiableModal] = useState(false);
    const [descriptionModal, setDescriptionModal] = useState("");
    const [imagesModal, setImagesModal] = useState([]);
    const [getIdAddModal, setGetIdAddModal] = useState("");
    const fileField = useRef();


    useEffect(() => {
        const getCategories = async () => {
            const cats = await api.getCategories();
            setCategories(cats);
            console.log(cats);
        }
        getCategories();
    }, [])

    useEffect(() => {
        const getUser = async () => {
            const json = await api.getUser();
            setUserInfo(json);
            setAdList(json.ads);
        }
        getUser();
    }, []);

    useEffect(() => {
        const getState = async () => {
          const response = await api.getStates();
          setState(response);
        }
        getState()
        // eslint-disable-next-line react-hooks/exhaustive-deps
      }, [])
    
      useEffect(() => {
        if (user) {
          // eslint-disable-next-line array-callback-return
          state.map(function (item) {
            if (item.name === user.state) {
              setStateUser(item._id);
    
            }
          })
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
      }, [user])

    function openModal(props) {

        categories.map((item) => {
            if (item.slug === props.category) {
                setCategoryModal(item._id);
            }
            return "";
        }
        )

        setAdStatusModal(props.status);
        setAdTitleModal(props.title);
        setPriceNegotiableModal(props.priceNegotiable);
        setPriceModal(props.price);
        setDescriptionModal(props.description);
        setImagesModal(props.images);
        setGetIdAddModal(props.id);

        setVisibleModal(!visibleModal);
    }



    return (
        <>
            <PageContainer>
                <PageArea>
                    <div className="userInfo">
                        <h2 className='title'>Olá, {info.name}</h2>
                        <div className='user'>
                            <div className='avatar'>
                                <img src="https://i.imgur.com/pYxq5za.jpg" alt="" />
                            </div>
                            <div className='userInfosAndEdit'>
                                <div className="actualInfos">
                                    <h1>Informações Atuais</h1>
                                    <table>
                                        <tr>
                                            <td className='tdLabel'>
                                                Nome:
                                            </td>
                                            <td>
                                                {info.name}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Email:
                                            </td>
                                            <td>
                                                {info.email}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Estado:
                                            </td>
                                            <td>
                                                {info.state}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Anuncios:
                                            </td>
                                            <td>
                                                50
                                            </td>
                                        </tr>
                                    </table>
                                    <div className="button">
                                        <button className="openModal">Editar Informações</button>
                                    </div>
                                </div>
                                {/* essa parte sera um modal  */}
                                {/* <div className="editInfos">
                                    <h1>Editar Informações</h1>
                                    <table>
                                        <tr>
                                            <td className='tdLabel'>
                                                Nome:
                                            </td>
                                            <td>
                                                {info.name}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Email:
                                            </td>
                                            <td>
                                                {info.email}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Estado:
                                            </td>
                                            <td>
                                                {info.state}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Anuncios:
                                            </td>
                                            <td>
                                                50
                                            </td>
                                        </tr>
                                    </table>
                                </div> */}
                            </div>
                        </div>
                    </div>
                </PageArea>
                <PageArea>
                    <div className='showAds'>
                        <h1>Meus anúncios</h1>
                        <div className='userAds'>
                            {adList.map((i, k) => (
                                <>
                                    <EditItem key={k} data={i} onClick={() => openModal(i)} />
                                </>
                            ))}
                        </div>
                    </div>
                    {visibleModal &&
                        <Modal title={adTitleModal} visibleModal={visibleModal} setVisibleModal={setVisibleModal}>
                            <form>

                            </form>
                        </Modal>
                    }
                </PageArea>
            </PageContainer>
        </>
    );
}

export default Page;
