import styled from 'styled-components';

export const FooterArea = styled.div`
    background-color:#AC7FDB;
    display:flex;
    justify-content:center;
    align-items:center;
    color:#FFF;
    font-size:13px;
    bottom:0;
    left:0;
    right:0;

    .list{
        list-style:none;
        display:flex;
        margin:0;
        padding:0;
    }
    .creditos p{
        text-center;
    }
`;
