import styled from 'styled-components';

export const Item = styled.div`

.aditem{

        width:25%;
        text-align:center;
        background-color:#f7f5f7;
        padding:8px;

        a{
            text-decoration:none;
            color:#000;
        }

        &:hover{
            background-color:#dba2db;
        }
    }

`;
