import styled from 'styled-components';

export const PageArea = styled.div`

    form{
        background-color:#FFF;
        border-radius:3px;
        padding:10px;
        box-shadow:0px 0px 3px #999;

        .area{
            display:flex;
            align-items:center;
            padding:10px;
            max-width:500px;
        }
        .area--title{
            width:200px;
            text-align:right;
            padding-right:20px;
            font-weight:bold;
            font-size:15px;
        }
        .area--input{
            flex:1;
            
            input{
                width:100%;
            }
        }
    }

`;
