import React, { useState, useEffect } from 'react';
import { PageArea, SearchArea } from './styled';
import { PageContainer } from '../../components/MainComponents';
import useApi from '../../helpers/MBSApi';
import { Link } from 'react-router-dom';
import AdItem from '../../components/partials/AdItem';


const Page = () => {

    const api = useApi();

    const [stateList, setStateList] = useState([]);
    const [stateLocal, setStateLocal] = useState();
    const [categories, setCategories] = useState([]);
    const [adList, setAdList] = useState([]);

    useEffect(() => {
        const getStates = async () => {
            const slist = await api.getStates();
            setStateList(slist);
        }
        getStates();
    }, []);

    useEffect(() => {
        const getCategories = async () => {
            const cats = await api.getCategories();
            setCategories(cats);
        }
        getCategories();
    }, []);

    useEffect(() => {
        const getRecentAds = async () => {
            const json = await api.getAds({
                sort: 'desc',
                limit: 8
            });
            setAdList(json.ads);
        }
        getRecentAds();
    }, []);

    return (
        <>
            <SearchArea>
                <PageContainer>
                    
                </PageContainer>
            </SearchArea>
            <PageContainer>
                <PageArea>
                    <h2>Anúncios Recentes</h2>
                    <div className="list">
                        {adList.map((i, k) =>
                            <AdItem key={k} data={i} />
                        )}
                    </div>
                    <Link to="/ads" className="seeAllLink">
                        Ver todos
                    </Link>
                    <hr />
                    <div>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas gravida ut nunc sed accumsan.
                        Nam sed odio bibendum, porta nulla ac, convallis velit.
                    </div>
                    <hr />
                </PageArea>
            </PageContainer>
        </>
    );
}

export default Page;
