import { Item } from './styled';
import { Link } from 'react-router-dom';
import useApi from '../../../helpers/MBSApi';
import Modal from "react-bootstrap/Modal";
import React, { useState, useRef, useEffect } from 'react';
// import MaskedInput from 'react-text-mask';
// import createNumberMask from 'text-mask-addons/dist/createNumberMask';

export default (props) => {
    const api = useApi();
    const [isOpen, setIsOpen] = useState(false);
    let price = '';
    // dados do usuario
    const [info, setUserInfo] = useState({});
    const [adList, setAdList] = useState([]);


    //dados para edição dos dados do usuario
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [state, setState] = useState([]);
    const [password, setPassword] = useState('');

    //dados para edição de anuncios
    const fileField = useRef();
    const [title, setTitle] = useState('');
    const [category, setCategory] = useState('');
    const [newPrice, setPrice] = useState('');
    const [priceNegotiable, setPriceNegotiable] = useState(false);
    const [desc, setDesc] = useState('');


    useEffect(() => {
        const getUser = async () => {
            const json = await api.getUser();
            setUserInfo(json);
            setAdList(json.ads);
        }
        getUser();
    }, []);

    const handleClick = () => {
        window.location.href = `/ad/${props.data.id}`;
    }

    const showModal = () => {
        setIsOpen(true);
    }
    const hideModal = () => {
        setIsOpen(false);
    };

    const handleNewAd = () => {

    };

    

    if (props.data.priceNegotiable) {
        price = "Preço negociável";
    } else {
        price = `R$ ${props.data.price}`
    }


    return (
        <Item className="aditem">
            <Link to={`/ad/${props.data.id}`} onClick={handleClick}>
                <div className="p-2 m-2">
                    <div className="item-image">
                        {
                            props.data.images &&
                            props.data.images.map((image, index) =>
                                <img key={index} src={`http://alunos.b7web.com.br:501/media/${image.url}`} alt="" />)
                        }
                        <img src={props.data.image} alt="" />
                    </div>
                    <div className="item-name">
                        {props.data.title}
                    </div>
                    <div className="item-price">
                        {price}
                    </div>
                </div>

            </Link>
            <button onClick={showModal} type="button" class="btn btn-primary">Editar</button>
            <Modal show={isOpen} onHide={hideModal}>
                <Modal.Header>Edite seu anúncio</Modal.Header>
                <form>
                    <Modal.Body>
                        <div className="mb-3">
                            <label for="inputTitle" className="form-label">
                                Título do anúncio:
                                <input
                                    type="text"
                                    className="form-control"
                                    id="inputTitle"
                                    aria-describedby="titleHelp" />
                                <div id="Help" className="form-text">O título atual é: {props.data.title}</div>
                            </label>
                        </div>
                        <div className="mb-3">
                            <label for="inputTitle" className="form-label">
                                Categoria:
                                <input
                                    type="text"
                                    className="form-control"
                                    id="inputTitle"
                                    aria-describedby="titleHelp" />
                                <div id="Help" className="form-text">Categoria atual: {props.data.category}</div>
                            </label>
                        </div>
                        <div className="mb-3">
                            <label for="inputTitle" className="form-label">
                                Preço:
                                <input
                                    type="text"
                                    className="form-control"
                                    id="inputTitle"
                                    aria-describedby="titleHelp" />
                                <div id="Help" className="form-text">Preço atual: {props.data.price}</div>
                            </label>
                        </div>
                        <div className="mb-3">
                            <label for="inputTitle" className="form-label">
                                Negociável?
                                <input
                                    type="checkbox"
                                    className="mr-1"
                                    id="inputTitle"
                                    aria-describedby="Help" />
                            </label>
                        </div>
                        <div className="mb-3 ">
                            <label for="inputTitle" className="form-label">
                                Descrição:
                                <input
                                    type="text"
                                    className="form-control"
                                    id="inputTitle"
                                    aria-describedby="titleHelp" />
                                <div id="Help" className="form-text">O título atual é: {props.data.title}</div>
                            </label>
                        </div>
                        <div className="mb-3 ">
                            <label for="inputTitle" className="form-label">
                                Imagens:
                                <input
                                    type="file"
                                    className="form-control"
                                    id="inputTitle"
                                    aria-describedby="titleHelp"
                                    multiple
                                    ref={fileField} />
                                <div id="Help" className="form-text">Quantidade de imagens que o anúncio possui: {props.data.images.length}</div>
                            </label>
                        </div>
                    </Modal.Body>
                    <Modal.Footer>
                        <button type="button" className="btn btn-danger" onClick={hideModal}>Cancel</button>
                        <button type="submit" onClick={handleNewAd} className="btn btn-success">Save</button>
                    </Modal.Footer>
                </form>
            </Modal>
        </Item>
    );
}
