import styled from 'styled-components';

export const PageArea = styled.div`
.userInfo, .user{
    display:flex;
    flex-flow:column;
    align-items:center;
}
.avatar img{
    width:100px;
    height:100px;
    border-radius:50%;
    object-fit:cover;
}
.userInfosAndEdit{
    display:flex;
}
.actualInfos, .editInfos{
    flex:1;
}
.actualInfos h1, .editInfos h1{
    text-align:center;
    font-size:18px;
}
.actualInfos{
    background-color:#670696;
    padding:15px;
    border-radius:8px;
    color:#FFF;
}


`;
