import React, { useState, useRef, useEffect } from 'react';
import { PageArea } from './styled';
import { PageContainer } from '../../components/MainComponents';
import useApi from '../../helpers/MBSApi';
import AdItem from '../../components/partials/AdItem';
import { useParams } from 'react-router-dom';



const Page = () => {
    const api = useApi();

    // dados do usuario
    const [info, setUserInfo] = useState([]);
    const [adList, setAdList] = useState([]);
    const { id } = useParams();


    //dados para edição dos dados do usuario
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [state, setState] = useState([]);
    const [password, setPassword] = useState('');

    //dados para edição de anuncios
    const [categories, setCategories] = useState([]);
    const [title, setTitle] = useState('');
    const [category, setCategory] = useState('');
    const [price, setPrice] = useState('');
    const [priceNegotiable, setPriceNegotiable] = useState(false);
    const [desc, setDesc] = useState('');
    const fileField = useRef();

    useEffect(() => {
        const getUser = async () => {
            const json = await api.getUser(id, true);
            setUserInfo(json);
            setAdList(json.ads);
        }
        getUser();
    }, []);

    return (
        <>
            <PageContainer>
                <PageArea>
                    <div className="userInfo">
                        <h2 className='title'>Olá, {info.name}</h2>
                        <div className='user'>
                            <div className='avatar'>
                                <img src="https://i.imgur.com/pYxq5za.jpg" alt="" />
                            </div>
                            <div className='userInfosAndEdit'>
                                <div className="actualInfos">
                                    <h1>Informações Atuais</h1>
                                    <table>
                                        <tr>
                                            <td className='tdLabel'>
                                                Nome:
                                            </td>
                                            <td>
                                                {info.name}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Email:
                                            </td>
                                            <td>
                                                {info.email}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Estado:
                                            </td>
                                            <td>
                                                {info.state}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Anuncios:
                                            </td>
                                            <td>
                                                50
                                            </td>
                                        </tr>
                                    </table>
                                    <div className="button">
                                        <button className="openModal">Editar Informações</button>
                                    </div>
                                </div>
                                {/* essa parte sera um modal  */}
                                {/* <div className="editInfos">
                                    <h1>Editar Informações</h1>
                                    <table>
                                        <tr>
                                            <td className='tdLabel'>
                                                Nome:
                                            </td>
                                            <td>
                                                {info.name}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Email:
                                            </td>
                                            <td>
                                                {info.email}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Estado:
                                            </td>
                                            <td>
                                                {info.state}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td className='tdLabel'>
                                                Anuncios:
                                            </td>
                                            <td>
                                                50
                                            </td>
                                        </tr>
                                    </table>
                                </div> */}
                            </div>
                        </div>
                    </div>
                </PageArea>
                <PageArea>
                    <div className='showAds'>
                        <h1>Meus anúncios</h1>
                        <div className='userAds'>
                            {adList.map((i, k) => (
                                <img src={i.image[0]}/>
                            ))}
                        </div>
                    </div>
                </PageArea>
            </PageContainer>
        </>
    );
}

export default Page;
