import styled from 'styled-components';

export const Item = styled.div`
a{
    display:block;
    text-decoration:none;
    margin:10px;
    border:1px solid #CCC;
    color:#000;

    .item-image img{
        width:100%;
        border-radius:5px;
    }
    .item-name{
        font-weight:bold;
    }
    .item-price{

    }
}



`;
