import styled from 'styled-components';

export const HeaderArea = styled.div`
background-color:#FFF;
height:60px;
border-bootom:1px solid #CCC;

    a{
      text-decoration:none;  
    }
    .container{
        max-width:1000px;
        margin:auto;
        display:flex;
        align-items:center;
    }
    .mbs-logo{
        height:60px;
        object-fit:cover;
    }
    .logo{
        text-center;
        flex:1;
    }
    .logo a{
        display:block;
    }

    nav{
        padding-top:10px;
        padding-bottom:10px;

        ul, li{
            margin:0px 20px;
            padding:0;
            list-style:none;
        }
