import styled from 'styled-components';

export const PageArea = styled.div`
height:100vh;

.rightSide{
    flex:1;

    h2{
        margin-top:0;
        font-size:18px;
    }
    .listWarning {
        padding:30px;
        text-align:center;
    }
    .list{
        flex-wrap:wrap;
        display:flex;
    }
    .aditem{
        width:33%;
    }
    .pagination{
        display:flex;
        align-items:center;
        justify-content:center;
        flex-wrap:wrap;

        .pageItem{
            width:30px;
            height:30px;
            border:1px solid #670696;
            display:flex;
            align-items:center;
            justify-content:center;
            font-size:15px;
            margin-right:5px;
            margin-bottom:10px;
            border-radius:3px;
            cursor:pointer;
        }
        .pageItem:hover, .pageItem.active{
            background-color:#670696;
            color:#FFF;
        }
    }
}
`;
