import styled from 'styled-components';

export const HeaderArea = styled.div`
background-color:#FFF;
height:60px;
border-bootom:1px solid #CCC;

    a{
      text-decoration:none;  
    }
    .container{
        max-width:1000px;
        margin:auto;
        display:flex;
        align-items:center;
    }
    .mbs-logo{
        object-fit:cover;
    }
    .logo a{
        display:block;
    }
`;
