import { Item } from './styled';
import { Link } from 'react-router-dom';
import Modal from "react-bootstrap/Modal";
import React, { useState } from 'react';

export default (props) => {

    const [isOpen, setIsOpen] = useState(false);

    const handleClick = () => {
        window.location.href = `/ad/${props.data.id}`;
    }

    const showModal = () =>{
        
    }

    let price = '';
    if (props.data.priceNegotiable) {
        price = "Preço negociável";
    } else {
        price = `R$ ${props.data.price}`
    }


    return (
        <Item className="aditem">
            <Link to={`/ad/${props.data.id}`} onClick={handleClick}>
                <div className="p-2 m-2">
                    <div className="item-image">
                        {
                            props.data.images &&
                            props.data.images.map((image, index) =>
                                <img key={index} src={`http://alunos.b7web.com.br:501/media/${image.url}`} alt="" />)
                        }
                        <img src={props.data.image} alt="" />
                    </div>
                    <div className="item-name">
                        {props.data.title}
                    </div>
                    <div className="item-price">
                        {price}
                    </div>
                    <div className="btn-group m-2" role="group" aria-label="ads editor">
                        <button type="button" class="btn btn-primary">Editar</button>
                    </div>
                </div>
            </Link>
            <Modal>
                <Modal.Header>Hi</Modal.Header>
                <Modal.Body>Sexo</Modal.Body>
                <Modal.Footer>This is the footer</Modal.Footer>
            </Modal>
        </Item>
    );
}
