import styled from 'styled-components';

export const SearchArea = styled.div`
background-color:#AC7FDB;
border-bottom:#CCC;
padding:20px 0;


`;
export const PageArea = styled.div`
h2{
    font-size:20px;
}
.list{
    display:flex;
    flex-wrap:wrap;
    .aditem{
        width:25%;
        text-align:center;
    }
}
.seeAllLink{
    color:#000;
    text-decoration:none;
    font-weight:bold;
    display:inline-block;
}

`;
