import styled from 'styled-components';

export const HeaderArea = styled.div`
background-color:#FFF;
border:1px solid #FFF;
    a{
      text-decoration:none;  
    }
    .container{
        max-width:1000px;
        margin:auto;
        display:flex;
        align-items:center;
    }
    .mbs-logo{
        height:60px;
        object-fit:cover;
    }
    .logo{
        text-center;
        flex:1;
        display:flex;
        align-items:center;
    }
    .logo a{
        display:flex;
    }

    nav{
        display:flex;
        align-items:center;
        height:100%;

        ul, li{
            margin:0px 20px;
            padding:0;
            list-style:none;
        }
        ul{
            display:flex;
            align-items:center;
        }
        li{

            a, button{
                cursor:pointer;
                outline:none;
                border:0;
                background:none;
                color:#000;
                font-size:15px;
                transition:all ease 0.2s;

                &:hover{
                    color:#CCC;
                }

                &.button{
                    background-color:#670696;
                    color:#FFF;
                    padding:10px 10px;
                    border-radius:5px;
                }
            }
        }
    }
`;
