import styled from 'styled-components';

export const SearchArea = styled.div`

background-color:#AC7FDB;
padding:20px 0;

.post{
    background-color:#670696;
    color:#FFF;
}

input, select{
    border:2px solid #670696;
    outline:0;
}

    .categoryItem{
        padding:10px;
        text-decoration:none;
        background-color:#670696;
        transition:all ease 0.3s;

        a{
            color:#FFF;
            text-decoration:none;
        }
    }
}
`;
export const PageArea = styled.div`
h2{
    font-size:20px;
}
.list{
    display:flex;
    flex-wrap:wrap;

    .aditem{
        width:25%;
        text-align:center;
    }
}
.seeAllLink{
    color:#000;
    text-decoration:none;
    font-weight:bold;
    display:inline-block;
}
`;
